<script>
import { mapGetters } from 'vuex';

export default {
  props: {
    value: {
      default: null,
      type: Number
    }
  },
  computed: mapGetters({liters: 'filter/liters'}),
  methods: {
    onClick(houseId) {
      this.$emit('input', houseId);
    },
    activeClass(houseId) {
      return this.value === houseId ? 'active' : '';
    }
  }
}
</script>

<style scoped lang="stylus">
.liters-mini
  width 100%
  position relative
  margin-bottom 15px
  .liter
    cursor pointer
  .fill
    opacity 0
    transition opacity .15s ease-in-out
  .number
    fill #fff
    transition fill .15s ease-in-out
  .active
    .fill
      opacity 1
    .number
      fill #1f252f
  &:before
    content ''
    display block
    padding-top 122%
  svg
    size 100%
    absolute left 0 top 0
</style>
